// Group the prints by collection
var collections = {};
for (var i = 0; i < msg.payload.prints.length; i++)
{
    var obj = msg.payload.prints[i];
    if (collections[obj.collection_id] === undefined) {
        collections[obj.collection_id] = { name: obj.collection_name, prints: [] };
    }
    collections[obj.collection_id].prints.push(obj);
}
// Now we've got all the prints grouped by collection, flatten the collections into
// an array because that's all that mustache (the template for formatting) can handle
msg.collections = [];
for (const key of Object.keys(collections)) {
    msg.collections.push(collections[key]);
}
return msg;
